<?class RSS{	public function RSS()	{		require_once ('db_config_inc.php');	}		public function GetFeed()	{		return $this->getDetails() . $this->getItems();	}		private function dbConnect()	{		//DEFINE ('LINK', mysql_connect (DB_HOST, DB_USER, DB_PASSWORD));	}		private function getDetails()	{		$detailsTable = "rss_presidents";		$this->dbConnect($detailsTable);		$query = "SELECT * FROM ". $detailsTable;		$result = mysql_query($query);				while($row = mysql_fetch_array($result))		{			$details = '<?xml version="1.0" encoding="UTF-8" ?>					<rss version="2.0">						<channel>							<title>US Presidents</title>							<link>http://www.test.com/</link>							<description>stuff about presidents</description>							<language>en-us</language>';		}		return $details;	}		private function getItems()	{		$itemsTable = "rss_presidents";		$query = "SELECT * FROM ". $itemsTable;		$result = mysql_query($query);		$items = '';		while($row = mysql_fetch_array($result))		{			$items .= '<item>						 <title>'. $row["title"] .'</title>						 <link>'. $row["link"] .'</link>						 <description><![CDATA['. $row["description"] .']]></description>					 </item>';		}		$items .= '</channel>				 </rss>';		return $items;	}}?>